    private void setDefaultButtonColor_Hover(int indButtonFocused){
        loop1: for (int i = 1; i <= 3; i++) {
            JPanel button = getButtonByIndex(i);
            if (i == indButtonSel) continue;
            if (i != indButtonFocused)
                onMouseLeave(button);
            else
                onMouseHover(button);
        }
    }
    
    private void setDefaultButtonColor_Leave(){
        for (int i = 1; i <= 3; i++) {
            JPanel button = getButtonByIndex(i);
            if (i != indButtonSel)
                onMouseLeave(button);
        }
    }
    
    private void setDefaultButtonColor_Click(int indButtonClicked){
        for (int i = 1; i <= 3; i++) {
            JPanel button = getButtonByIndex(i);
            if (i != indButtonClicked)
                onMouseLeave(button);
            else
                onMouseClick(button);
        }
    }
    
    private JPanel getButtonByIndex(int indice){
        switch (indice)
        {
            case 1: return btnInicio;
            case 2: return btnCalcular;
            case 3: return btnAcerca;
            default: return null;
        }
    }
    
    private void selectPanel(){
        boolean visible;
        for (int i = 1; i <= 3; i++) {
            visible = false;
            JPanel panel = getPanelByIndex(i);
            if (i == indButtonSel)
                visible = true;
            panel.setVisible(visible);
        }
    }
    
    private JPanel getPanelByIndex(int indice){
        switch (indice)
        {
            case 1: return pInicio;
            case 2: return pCalcular;
            case 3: return pAcerca;
            default: return null;
        }
    }
    
    //<editor-fold defaultstate="collapsed" desc="User Events">
    private void onMouseHover(JPanel panel){
        panel.setBackground(new Color(178, 215, 116));
    }
    
    private void onMouseLeave(JPanel panel){
        panel.setBackground(new Color(67, 83, 108));
    }
    
    private void onMouseClick(JPanel panel){
        panel.setBackground(new Color(147, 186, 35));
    }
